<style lang="less">
    .powered-by {
        display: flex;
        align-items: center;
        justify-content: center;
        gap: 6px;
        font-size: 10px;
    }

    .powered-by__text {
        font-weight: 500;
    }

    .powered-by__link {
        display: inline-flex;
        align-items: center;
        text-decoration: none;
        transition: opacity 0.2s ease;
    }

    .powered-by__link:hover {
        opacity: 0.8;
    }

    .powered-by__logo {
        display: block;
    }

    // Dark variant (for light backgrounds like popups)
    .powered-by--dark {
        color: #6b6b6b;
    }

    .powered-by--dark .powered-by__link {
        color: #030310;
    }

    // Light variant (for dark backgrounds like settings panel)
    .powered-by--light {
        color: rgba(255, 255, 255, 0.6);
    }

    .powered-by--light .powered-by__link {
        color: rgba(255, 255, 255, 0.8);
    }

    .powered-by--light .powered-by__link:hover {
        color: rgba(255, 255, 255, 1);
    }
</style>
